package com.justclack.admobadschangerimport android.os.Bundleimport androidx.appcompat.app.AppCompatActivityimport android.view.Menuimport android.view.MenuItemimport android.widget.Toastimport com.justclack.adschnager.GetMyAdmobAdsimport com.justclack.adschnager.OnSuccessResponseimport com.justclack.adschnager.pojos.TestPoJoItemclass MainActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_main)        GetMyAdmobAds.getAds(            this,            "com.test.app",            "https://gist.githubusercontent.com/iammujahidkhan/d586c8901d8d4e1233be58c23961015c/raw/",            "raw/",            object :                OnSuccessResponse<TestPoJoItem> {                override fun changeAppId(rawResponse: TestPoJoItem) {                    Toast.makeText(this@MainActivity, "" + rawResponse.packageX, Toast.LENGTH_SHORT)                        .show()                }            })    }    override fun onCreateOptionsMenu(menu: Menu): Boolean {        menuInflater.inflate(R.menu.menu_main, menu)        return true    }    override fun onOptionsItemSelected(item: MenuItem) =        when (item.itemId) {            R.id.action_settings -> true            else -> super.onOptionsItemSelected(item)        }}