package com.justclack.admobadschangerimport android.os.Bundleimport androidx.appcompat.app.AppCompatActivityimport android.view.Menuimport android.view.MenuItemimport android.widget.Toastimport com.justclack.adschnager.GetMyAdmobAdsimport com.justclack.adschnager.OnSuccessResponseimport com.justclack.adschnager.pojos.Dataimport com.justclack.adschnager.pojos.RealAdsPoJoclass MainActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_main)        GetMyAdmobAds.getAds(            this,            "com.test",            "https://gist.githubusercontent.com/48b9647f2116b09803894bacaf82d729/",            "raw/",            object :                OnSuccessResponse<Data> {                override fun changeAppId(rawResponse: Data) {                    Toast.makeText(                        this@MainActivity,                        "" + rawResponse.type,                        Toast.LENGTH_SHORT                    )                        .show()                }            })    }    override fun onCreateOptionsMenu(menu: Menu): Boolean {        menuInflater.inflate(R.menu.menu_main, menu)        return true    }    override fun onOptionsItemSelected(item: MenuItem) =        when (item.itemId) {            R.id.action_settings -> true            else -> super.onOptionsItemSelected(item)        }}